INSERT INTO `jform_cgreport_head` VALUES ('402881885f0c77e5015f10fa58890080', 'SELECT \r\n    wv.create_date,\r\n   wv.order_id,\r\n    wv.ku_wei_bian_ma,\r\n    wv.bin_id,\r\n    wv.cus_code,\r\n    wv.zhong_wen_qch,\r\n    wv.shp_ming_cheng,\r\n    wv.goods_pro_data,\r\n    wv.base_goodscount,\r\n    wv.base_unit,\r\n    mb.ku_wei_lei_xing as leixing\r\nFROM\r\n    wv_stock_base wv,\r\n    md_bin mb\r\nwhere\r\n    wv.ku_wei_bian_ma = mb.ku_wei_bian_ma and\r\n    wv.kuctype = \'库存\'', 'wm_jck', '', '进出库报表', 'admin', '2018-03-26 11:51:47', 'admin', NULL, NULL, NULL, '', '', '', '1');
