INSERT INTO `jform_cgreport_head` VALUES ('402881885f23b8b8015f254064d2002f', ' select \r\n        `wg`.`create_date` AS `create_date`,\r\n        `wg`.`create_name` AS `create_name`,\r\n        `wg`.`create_by` AS `create_by`,\r\n         wg.im_notice_id,\r\n         wg.item_text,\r\n         wg.rec_deg,\r\n        `wg`.`bin_id` AS `ku_wei_bian_ma`,\r\n        `wg`.`tin_id` AS `bin_id`,\r\n        `wg`.`cus_code` AS `cus_code`,\r\n        `mc`.`zhong_wen_qch` AS `zhong_wen_qch`,\r\n        `mg`.`goods_id` AS `goods_code`,\r\n        `wg`.`goods_id` AS `goods_id`,\r\n        `wg`.`qm_ok_quat` AS `goods_qua`,\r\n        `mg`.`goods_name` AS `shp_ming_cheng`,\r\n        `wg`.`pro_data` AS `goods_pro_data`,\r\n        `mg`.`bzhi_qi` AS `bzhi_qi`,\r\n        `wg`.`goods_unit` AS `goods_unit`,\r\n        `wg`.`base_unit` AS `base_unit`,\r\n        `wg`.`base_goodscount` AS `base_goodscount`,\r\nmg.zhl_kg,\r\n(`wg`.`base_goodscount` * mg.zhl_kg) as sumzhl\r\n    from\r\n        ((`wm_in_qm_i` `wg`\r\n        join `md_cus` `mc`)\r\n        join `mv_goods` `mg`)\r\n    where\r\n        ((`wg`.`cus_code` = `mc`.`ke_hu_bian_ma`)\r\n            and (`wg`.`goods_id` = `mg`.`goods_code`)\r\n)  having base_goodscount > 0\r\n    ', 'wm_in_qm', '', '验收明细', 'admin', '2017-10-26 09:55:13', 'admin', NULL, NULL, NULL, '', '', '', '1');
INSERT INTO `jform_cgreport_head` VALUES ('402881885f23b8b8015f254836c60042', 'select \r\n        `wg`.`create_date` AS `create_date`,\r\n        `wg`.`create_name` AS `create_name`,\r\n        `wg`.`create_by` AS `create_by`,\r\n         wg.order_id,\r\n         (select im_cus_code from wm_im_notice_h where notice_id =  wg.order_id limit 1) as im_cus_code,\r\n        `wg`.`ku_wei_bian_ma` AS `ku_wei_bian_ma`,\r\n        `wg`.`bin_id` AS `bin_id`,\r\n        `wg`.`cus_code` AS `cus_code`,\r\n        `mc`.`zhong_wen_qch` AS `zhong_wen_qch`,\r\n\r\n        `mg`.`goods_id` AS `goods_code`,\r\n        `mg`.`goods_name` AS `shp_ming_cheng`,\r\n      mg.shp_gui_ge,\r\n        `wg`.`goods_pro_data` AS `goods_pro_data`,\r\n        `mg`.`bzhi_qi` AS `bzhi_qi`,\r\n        `wg`.`base_unit` AS `base_unit`,\r\n        `wg`.`base_goodscount` AS `base_goodscount`,\r\n  mg.zhl_kg\r\n    from\r\n        ((`wm_to_up_goods` `wg`\r\n        join `md_cus` `mc`)\r\n        join `mv_goods` `mg`)\r\n    where\r\n        ((`wg`.`cus_code` = `mc`.`ke_hu_bian_ma`)\r\n            and (`wg`.`goods_id` = `mg`.`goods_code`))\r\norder by `wg`.`create_date` desc\r\n   ', 'wm_to_up_d', '', '上架明细', '初始', '2018-11-26 09:24:22', 'admin', NULL, NULL, NULL, '', '', '', '1');
INSERT INTO `jform_cgreport_head` VALUES ('402881885f23b8b8015f254d22d80052', 'select \r\n        `wg`.`create_date` AS `create_date`,\r\n        `wg`.`create_name` AS `create_name`,\r\n\r\n        wg.order_id,\r\n      (select im_cus_code from wm_om_notice_h where om_notice_id = wg.order_id limit 1) as im_cus_code,\r\n        `wg`.`ku_wei_bian_ma` AS `ku_wei_bian_ma`,\r\n        `wg`.`bin_id_from` AS `bin_id`,\r\n        wg.bin_id_to,\r\n        `wg`.`cus_code` AS `cus_code`,\r\n        `mc`.`zhong_wen_qch` AS `zhong_wen_qch`,\r\n        `mg`.`goods_id` AS `goods_code`,\r\n\r\n \r\n        `mg`.`goods_name` AS `shp_ming_cheng`,\r\n  mg.shp_gui_ge,\r\n        `wg`.`goods_pro_data` AS `goods_pro_data`,\r\n        `mg`.`bzhi_qi` AS `bzhi_qi`,\r\n\r\n        `wg`.`base_unit` AS `base_unit`,\r\n        `wg`.`base_goodscount` AS `base_goodscount`,\r\nmg.zhl_kg\r\n    from\r\n        ((`wm_to_down_goods` `wg`\r\n        join `md_cus` `mc`)\r\n        join `mv_goods` `mg`)\r\n    where\r\n        ((`wg`.`cus_code` = `mc`.`ke_hu_bian_ma`)\r\n            and (`wg`.`goods_id` = `mg`.`goods_code`))\r\norder by `wg`.`create_date` desc', 'wm_to_down', '', '下架明细', '初始', '2018-11-26 09:23:21', 'admin', NULL, NULL, NULL, '', '', '', '1');
INSERT INTO `jform_cgreport_head` VALUES ('402881885f2de2c8015f2dedf564031c', 'select \r\n    wc.cus_code,\r\n    wc.cus_name,\r\n    wc.cost_code,\r\n    wc.cost_name,\r\n    wc.cost_data,\r\n     FORMAT(sum(wc.day_cost_yj),2) as yuanj,\r\n     FORMAT(sum(wc.day_cost_bhs),2) as bhsj,\r\n     FORMAT(sum(wc.day_cost_se),2) as shuie,\r\n     FORMAT(sum(wc.day_cost_hsj),2) as hansj\r\nfrom\r\n    wm_day_cost wc\r\ngroup by wc.cus_code , wc.cus_name , wc.cost_code , wc.cost_name , wc.cost_data', 'wm_month_cost', '', '费用合计', 'admin', '2017-10-20 11:58:06', 'admin', NULL, NULL, NULL, '', '', '', '1');
INSERT INTO `jform_cgreport_head` VALUES ('402881885f2de2c8015f2df70d8b032c', 'select \'卸货费\' as feiyong,wi.create_name, wi.create_date, wi.im_notice_id,    wi.goods_id, mg.shp_ming_cheng,   sum(wi.qm_ok_quat) as qmcount, format((sum(wi.qm_ok_quat) * mg.zhl_kg  ),2) as grcount,   format((sum(wi.qm_ok_quat) * mg.zhl_kg *11/1000 ),2) as grcountfy \r\n					,\'11\'as danjia from    wm_in_qm_i wi,    mv_goods mg where  wi.goods_id = mg.goods_id and wi.qm_ok_quat > 0 \r\n					 group by wi.create_name,wi.im_notice_id ,wi.cus_code,  wi.goods_id ', 'wm_inqm_feiy', '', '卸货费', 'admin', '2017-10-22 21:29:13', 'admin', NULL, NULL, NULL, '', '', '', '1');
INSERT INTO `jform_cgreport_head` VALUES ('402881885f2de2c8015f2dfbc1610339', 'select \r\n    \'装车费\' as feiyong,\r\n    wi.create_name,\r\n    wi.create_date,\r\n    wi.order_id,\r\n    wi.goods_id,\r\n    mg.shp_ming_cheng,\r\n    sum(wi.base_goodscount) as qmcount,\r\n    format((sum(wi.base_goodscount) * mg.zhl_kg),\r\n        2) as grcount,\r\n    format((sum(wi.base_goodscount) * mg.zhl_kg * 11 / 1000),\r\n        2) as grcountfy,\r\n  \'11\'  as  danjia\r\nfrom\r\n    wm_to_down_goods wi,\r\n    mv_goods mg\r\nwhere\r\n    wi.goods_id = mg.goods_id\r\n        and wi.base_goodscount > 0\r\nand wi.order_id <> \'ZY\'\r\ngroup by wi.create_name , wi.order_id , wi.cus_code , wi.goods_id', 'wm_to_downfy', '', '装车费', 'admin', '2017-10-22 21:31:50', 'admin', NULL, NULL, NULL, '', '', '', '1');
INSERT INTO `jform_cgreport_head` VALUES ('402881885f2de2c8015f2e3f81d70366', 'select \r\n    wc.cus_code,\r\n    wc.cus_name,\r\n    wc.cost_code,\r\n    wc.cost_name,\r\n     FORMAT(sum(wc.day_cost_yj),2) as yuanj,\r\n     FORMAT(sum(wc.day_cost_bhs),2) as bhsj,\r\n     FORMAT(sum(wc.day_cost_se),2) as shuie,\r\n     FORMAT(sum(wc.day_cost_hsj),2) as hansj\r\nfrom\r\n    wm_day_cost wc\r\ngroup by wc.cus_code , wc.cus_name , wc.cost_code , wc.cost_name ', 'wm_sum_hejii', '', '合计', 'admin', '2017-10-20 12:01:20', 'admin', NULL, NULL, NULL, '', '', '', '1');
INSERT INTO `jform_cgreport_head` VALUES ('402881885f3f9203015f450115f502c6', 'select ws.*, count(*) as count from wv_stock_base ws where ws.kuctype = \'库存\' and ws.create_date >=${begin_date} and ws.create_date <=${end_date}  group by ws.cus_code ,ws.goods_id ', 'jckcs', '', '进出库次数', 'admin', '2017-10-23 00:54:17', 'admin', NULL, NULL, NULL, '', '', '', '1');
INSERT INTO `jform_cgreport_head` VALUES ('402881885f49c21c015f4e9994ee085e', 'SELECT \r\n    wh.his_date,\r\n    wh.cus_code,\r\n    mc.zhong_wen_qch,\r\n    wh.ku_wei_bian_ma,\r\n    wh.bin_id,\r\n    wh.goods_id,\r\n    mg.shp_ming_cheng,\r\n    wh.count,\r\n    wh.base_unit,\r\n(mg.zhl_kg / mg.chl_shl) as zhl_kg\r\nFROM\r\n    wm_his_stock as wh,\r\n    md_cus  as mc,\r\n    md_goods as mg\r\nwhere  wh.cus_code = mc.ke_hu_bian_ma   and wh.goods_id = mg.shp_bian_ma', 'wm_his_stock', '', '计费历史库存', 'admin', '2018-01-15 21:03:35', 'admin', NULL, NULL, NULL, '', '', '', '1');
