INSERT INTO `jform_graphreport_head` VALUES ('402881885f3f9203015f44ee804402ba', 'SELECT \r\n    bs.store_code,\r\n    (select \r\n            sum(zui_da_tuo_Pan)\r\n        from\r\n            md_bin mb\r\n        where\r\n            mb.bin_store = bs.store_code\r\n                and mb.zui_da_tuo_Pan < 99999) as tuopansum,\r\n    (select \r\n            count(*)\r\n        from\r\n            wv_stock wv\r\n       where wv.bin_store  = bs.store_code ) as usecount\r\nfrom\r\n    ba_store bs\r\nhaving  tuopansum > 0', 'CWSYL', '储位使用率', '储位使用率', '托盘', 'store_code', 'Y', '');
